/// <reference types="node"/>

/** https://url.spec.whatwg.org/#url-representation */
export interface URLRecord {
    scheme: string;
    username: string;
    password: string;
    host: string | number | IPv6Address | null;
    port: number | null;
    path: string[];
    query: string | null;
    fragment: string | null;
    cannotBeABaseURL?: boolean | undefined;
}

